import Style from "./services.module.css";
import ServiceCards from "../../components/serviceCards/ServiceCards";
import { Link } from 'react-router-dom';
import Loader from  "../../assets/images/loader.jpg";
import { serviceProvider } from '../../api/service';
import React, { useEffect, useState } from 'react';
function Services() {
   const [list, setList] = useState([]);
   const [lat, setLat] = useState([]);
   const [lng, setLng] = useState([]);
   const [loading, setLoading] = useState(true);

   useEffect(() => {
      serviceProvider().then(responce => {
         const listed=responce.Data.organizations
         setList(listed);
         setLoading(false);
   
       });

       navigator.geolocation.getCurrentPosition(function(position) {
         setLat( position.coords.latitude);
         setLng( position.coords.latitude);
          //Current Latitute  position.coords.latitude
          //Current Longitude position.coords.longitude
          calcCrow(position.coords.latitude,position.coords.longitude,'30.8138','73.4534');
        
       });

       function calcCrow(lat1, lon1, lat2, lon2) {
         var R = 6371 // km
         var dLat = toRad(lat2 - lat1)
         var dLon = toRad(lon2 - lon1)
         var lat1 = toRad(lat1)
         var lat2 = toRad(lat2)
     
         var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
             Math.sin(dLon / 2) * Math.sin(dLon / 2) * Math.cos(lat1) * Math.cos(lat2)
         var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a))
         var d = R * c
         console.log(d);
         // return d
     }
     
     // Converts numeric degrees to radians
     function toRad(Value) {
         return Value * Math.PI / 180
     }
        
    }, [])


   return (
      <>
         <div className={Style.home}>
         <div className="topHeading">Service Providers</div>
         <div className={Style.cardMain}>
         {/* <img src={Loader} alt="profileImg"  className="loader" /> */}
         { loading ? <img src={Loader} alt="profileImg"  className="loader" /> : null }

            <Link to="/detail" className={Style.serviceLink}>
            {list.map(function(object, i){
        return (
        <>
        <ServiceCards obj={object}  key={i} 
        />
        </>
        )
})}
            </Link>
         </div>
      </div>
         
      </>
   )
}

export default Services
